import React,{Component} from 'react';
import Navigation from './Components/Navigation/Navigation';
import FaceRecognition from './Components/FaceRecognition/FaceRecognition';
import Logo from './Components/Logo/Logo';
import Clarifai from 'clarifai';
import ImageLinkForm from './Components/ImageLinkForm/ImageLinkForm';
import Rank from './Components/Rank/Rank';
import Particles from 'react-particles-js';

import './App.css';
const app=new Clarifai.App({
  apiKey:'d946ffbdf438480a8197b49ced9268a4'
});


const particlesOptions={
  particles: {
  number: {
    value: 50,
    density: {
      enable: true,
      value_area: 500 // Denser the smaller the number.
    }
  },
 }
}

class App extends Component{
  constructor(){
    super();
    this.state={
      input: '',
      imageURL:''
    }
  }
  //onInputChange is a property of app
  onInputChange=(event) =>{
    console.log(event.target.value);
  }

  onButtonSubmit=()=>{
    this.setState({imageURL: this.state.input})
    app.models.predict(Clarifai.COLOR_MODEL, "https://samples.clarifai.com/face-det.jpg").then(
    function(response) {
      // do something with response
      console.log(response);
    },
    function(err) {
      // there was an error
    }
  );
  }
    render(){
    return (
      <div className="App">
        <Particles  className='particles'
          params={{particlesOptions}}
         />
        <Navigation />
        <Logo />
         <Rank />
        <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>

        <FaceRecognition imageURL={this.state.imageURL} />

      </div>
    );
  }
}

export default App;
